.raised {
  text-shadow: 0px 1px 3px rgba(0,0,0,0.8);
  mask-image: gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
  -webkit-mask-image: -webkit-gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
  -moz-mask-image: -moz-gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
  -o-mask-image: -o-gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
}
html {
  min-height: 100%;
}
body {
  background: #d5f1ff;
  background: -moz-radial-gradient(center 0,circle cover,#d5f1ff,#79d1ff);
  background: -webkit-radial-gradient(center 0,circle cover,#d5f1ff,#79d1ff);
  background: radial-gradient(center 0,circle cover,#d5f1ff,#79d1ff);
  color: #00547F;
  font-family: Georgia, serif;
  font-size: 110%;
  padding: 1em;
  min-height: 100%;
}
code {
  background: #6fcdff;
  padding: 0.1em 0.2em;
}
blockquote {
  color: #26617F;
  background: #6fcdff;
  padding: 0.5em;
}
#container a {
  color: #26617F;
  font-weight: bold;
  text-decoration: none;
  padding: 0.1em 0.2em;
}
#container a:visited {
  color: #0f2530;
}
#container a:hover {
  color: #A2DFFF;
  background: #00547F;
}
#header h1 {
  text-shadow: 0px 1px 3px rgba(0,0,0,0.8);
  mask-image: gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
  -webkit-mask-image: -webkit-gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
  -moz-mask-image: -moz-gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
  -o-mask-image: -o-gradient(linear,left bottom,left top,color-stop(0,rgba(0,0,0,0.8)),color-stop(0.6,white));
  margin: 0.5em auto;
  font-family: Lobster, 'Trebuchet MS', 'Comic Sans MS', sans-serif;
  font-size: 4em;
  text-align: center;
  font-weight: bold;
  color: #00547F;
}
#header h1 a {
  color: #00547F;
}
#header h1 a:visited {
  color: #00547F;
}
#header h1 a:hover {
  background: none;
  color: #00547F;
}
#content #content-inner {
  margin: 1em 2em;
}
#content p a {
  border-bottom: 1px dotted #26617F;
}
#content h2 {
  font-family: 'Lobster', 'Trebuchet MS', sans-serif;
  letter-spacing: 0.07em;
  margin-bottom: 0.1em;
  color: #001119;
}
#footer {
  font-size: 0.8em;
  text-transform: lowercase;
  text-align: center;
  clear: both;
}
#footer p {
  margin: 1em;
}
#menu .menu {
  background: #00547F;
  box-shadow: 0px 1px 3px rgba(0,0,0,0.8);
  border: 2px solid #004366;
  border-radius: 8px;
  padding: 1em 0;
}
#menu ul {
  margin: 0;
  padding: 0;
  list-style: none;
}
#menu a {
  display: block;
  padding: 1em 2em;
}
#menu a:link,
#menu a:visited {
  color: #A2DFFF;
}
#menu a:hover {
  background: #004366;
}
.post hr {
  background: #26617F;
  margin-bottom: 1.5em;
}
.post-meta {
  text-transform: uppercase;
  font-size: 0.7em;
  font-family: Verdana, sans-serif;
  font-weight: bold;
  color: #26617F;
  margin: 0 0 1.5em 0.5em;
}
.post-more {
  margin-bottom: 1.5em;
}
